@Injectable()
export class FakeBackendInterceptor implements HttpInterceptor {
    intercept(request: HttpRequest<any>, next: HttpHandler): Observable<HttpEvent<any>> {
        const { url, method, headers, body } = request;
        
        return of(null)
            .pipe(mergeMap(handleRoute))
            .pipe(materialize())
            .pipe(delay(500))
            .pipe(dematerialize());

        function handleRoute() {
            switch (true) {
                case url.endsWith('/api/authenticate') && method === 'POST':
                    return authenticate();
                case url.endsWith('/api/accounts/transactions') && method === 'POST':
                    return transaction();
                case url.endsWith('/api/jwt/authenticate') && method === 'GET':
                    return authenticateJwt();
                case url.endsWith('/api/request/product') && method === 'POST':
                    return newProduct();
                case url.endsWith('/api/accounts') && method === 'POST':
                    return accounts();
                default:
                    // pass through any requests not handled above
                    return next.handle(request);
            }    
        }

        // route functions

        function authenticate() {
            const { username, password } = body;
            const user = users.find(x => x.identity === username && x.password === password);
            if (!user) return error('Username or password is incorrect');
            delete user.password;
            user.jwt = 'fake-jwt-token'
            return ok(user)
        }

        function authenticateJwt() {
            if(!isLoggedIn) return unauthorized();
            const jwt  = headers.get('Authorization').split(" ")[1];
            const user = users.find(x => x.jwt === jwt);
            if (!user) return error('Session expired');
            delete user.password;
            user.jwt = 'fake-jwt-token'
            return ok(user)
        }

        function transaction() {
            if (!isLoggedIn()) return unauthorized();
            const {idAccount} = body;
            let transaction = transactions.filter(x => x.idAccount === idAccount);
            if(transaction.length === 0) return error('Without transactions')
            return ok(transaction);
        }

        function newProduct() {
            if (!isLoggedIn()) return unauthorized();
            const {identity, product, cellphone, monthly } = body;
            let accountsUsers = accountsForUser.filter(x => x.idUser === identity);
            accountsUsers[0].accountsUser.push({
                id: getRandom(),
                name: 'test',
                icon: products.find(x => x.name == product).icon,
                type: product,
                status: 'Pending',
                currency: 'USD',
                balance: monthly,
                transactions: []
            })
            return ok();
        }

        function accounts() {
            if (!isLoggedIn()) return unauthorized();
            const { identity } = body;
            let accountsUsers = accountsForUser.filter(x => x.idUser === identity);
            return ok(accountsUsers[0].accountsUser);
        }

        // helper functions

        function getRandom(){
            return (Math.random()*Math.pow(10, 9))+''
        }

        function ok(body?) {
            return of(new HttpResponse({ status: 200, body }))
        }

        function error(message) {
            return throwError(new HttpErrorResponse({ error: { message } }));
        }

        function unauthorized() {
            return throwError({ status: 401, error: { message: 'Unauthorised' } });
        }

        function isLoggedIn() {
            return headers.get('Authorization') === 'Bearer fake-jwt-token';
        }

    }
}

export const fakeBackendProvider = {
    provide: HTTP_INTERCEPTORS,
    useClass: FakeBackendInterceptor,
    multi: true
};